const Home = () => {

    const [user_id,setUserId] = useState('');
    const [userName,setUserName] = useState('');
    const [hasSession,setHasSession] = useState(false);
    const [cart, setCart] = useState(0);
    const [,setMessage] = useState('');



    const validateSession = async () => {

        if(sessionStorage.getItem("id")){
            await setUserId(sessionStorage.getItem("id"));
            await setUserName(sessionStorage.getItem("name"));
            await setHasSession(true);
            getUser();
        }

    };

    useEffect(() => {
        validateSession();
    }, [validateSession]);

    const getUser = () => {

        fetch('http://localhost/backend/getUserById.php', {
            method: 'GET',
            headers: {
            'Content-Type': 'application/json'
            },body: JSON.stringify({
                user_id: user_id
            }) 
        }).then( (response) => {
            return response.json()
        })
        .then( (data) => {
            console.log(data)
            setUserName(data.firstName);
            setHasSession(true);  
        }).catch(() => setMessage('Usuario invalidooo'));
    }

    return (
        <>
           <AppContext.Provider value={{cart,setCart,userName }}>
             <BrowserRouter>
                 <Header name = {userName} session = {hasSession}/>
                 <MenuBar session = {hasSession}/>
                 <Routes>
                 {/* <Route exact path="/" element={<Home/>}/> */}
                 <Route exact path="/login" element={<Login/>} />
                 <Route path="/createAccount" element={<CreateAccount />} /> 
                 <Route path="/myAccount" element={<MyAccount />} /> 
                 <Route path="/addProduct" element={<AddProduct />} /> 
                 <Route path="/productFeed" element={<ProductsFeed />} /> 
                 </Routes>
             </BrowserRouter>
             </AppContext.Provider>
             {/* <BrowserRouter>
                 <Routes> */}
                     {/* <MenuBar session = {this.state.hasSession}/> */}
                 {/* </Routes>
             </BrowserRouter> */}
             {/* <CustomerListOfProduts/> */}
         </>
    )
}


export default Home;
