import styled from "styled-components";

export const Container = styled.div`

    width: 100%;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 2%;
    table {
        border: 2px solid forestgreen;
        width: 800px;
        height: 200px;
    }

    th {
        /* border-bottom: 1px solid black; */
        background-color: rgb(20, 215, 179);
        color: white;
    }
    tr:hover {
        background-color: rgb(20, 215, 179);
        color: white;
    }
    td {
        text-align: center;
    }
`;
