    }, [productList]);

    return (
        <Container>
            <h1>My Products</h1>
            <table>
                <tr>
                    <th>Name</th>
                    <th>Description</th>
                    <th>Price</th>
                    <th>Quantity</th>
                    <th>Category</th>
                    <th></th>
                </tr>
                {productList.map((product, key) => {
                    return (
                        <tr key={product.ProductID}>
                        <td>{product.ProductName}</td>
                        <td>{product.ProductDescription}</td>
                        <td>{product.ProductPrice}</td>
                        <td>{product.ProductQuantity}</td>
                        <td>{product.ProductCategory}</td>
                        <td><button id='btnEdit'>Edit</button> <button onClick={() => handleDelete(product, key)} id='btnDelete'>Delete</button></td>
                        </tr>
                    )
                })}
            </table>
        </Container>

    );
}

