import React, {useEffect, useState, useCallback}from "react";
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Header from './Header';
import MenuBar from './MenuBar';
// import CustomerListOfProduts from "./CustomerListOfProduts";
import CreateAccount from './CreateAccount';
import MyAccount from './MyAccount'
import Login from './Login';
import AddProduct from "./AddProduct";
import ProductsFeed from "./ProductsFeed";
import { AppContext} from './AppProvider';
import MyProduct from "./MyProduct";

const Home = () => {

    const [user_id,setUserId] = useState('');
    const [userName,setUserName] = useState('');
    const [hasSession,setHasSession] = useState(false);
    const [cart, setCart] = useState(0);
    const [categoryType,setCategoryType] = useState("All");
    const [,setMessage] = useState('');



    // const validateSession = async () => {


    //     }

    // };



    const getUser = useCallback(() => {

        fetch('http://localhost/backend/getUserById.php', {
            method: 'GET',
            headers: {
            'Content-Type': 'application/json'
            },body: JSON.stringify({
                user_id: user_id
            }) 
        }).then( (response) => {
            return response.json()
        })
        .then( (data) => {
            console.log(data)
            setUserName(data.firstName);
            setHasSession(true);  
        }).catch(() => setMessage('Usuario invalidooo'));
    },[user_id]);

    useEffect(() => {
        if(sessionStorage.getItem("id")){
             setUserId(sessionStorage.getItem("id"));
             setUserName(sessionStorage.getItem("name"));
             setHasSession(true);
            getUser();
        }
    }, [getUser]);

    return (
        <>
           <AppContext.Provider value={{cart,setCart,userName, categoryType, setCategoryType }}>
             <BrowserRouter>
                 <Header name = {userName} session = {hasSession}/>
                 <MenuBar session = {hasSession}/>
                 {/* <ProductsFeed /> */}
                 <Routes>
                 {/* <Route exact path="/" element={<Home/>}/> */}
                 <Route path="/login" element={<Login/>} />
                 <Route path="/createAccount" element={<CreateAccount />} /> 
                 <Route path="/myAccount" element={<MyAccount />} /> 
                 <Route path="/addProduct" element={<AddProduct />} /> 
                 <Route path="/productFeed" element={<ProductsFeed />} /> 
                 <Route path="/myProducts" element={<MyProduct />} /> 
                 </Routes>
             </BrowserRouter>
             </AppContext.Provider>
             {/* <BrowserRouter>
                 <Routes> */}
                     {/* <MenuBar session = {this.state.hasSession}/> */}
                 {/* </Routes>
             </BrowserRouter> */}
             {/* <CustomerListOfProduts/> */}
         </>
    )
}


export default Home;

