import React, {useState, useContext } from 'react';
import CartItem from './CartItem';
import { Container } from './MyCartStyle';
import { AppContext } from './AppProvider'
// import '../Styles/productFeed.css';

const MyCart = () =>   {

const { cart, setCart , cartPrice, setCartPrice} = useContext(AppContext);


const [checkout, setCheckout] = useState(false);

const goToCheckout = () =>{
    setCheckout(true);

    console.log("entrei no checkout" + checkout);
}



    return (
        <Container>
            <div id='myCart'>
                <h1>My Cart</h1>
                <p>My items:</p>
                <ul className='productList'>
                    {Object.entries(cart).map(([key,product]) => (<CartItem className="productItem" key={key} productItem = {product} 
                    />))}
                </ul>
                <p>Total price: ${cartPrice}</p>
                <button id="btnCheckout" onClick={()=> goToCheckout()}>Checkout</button>
            </div>
            {checkout &&
                    <form  className="checkout" >
                        <div className='rowCheckout'>
                            <label className = "labelCheckout" htmlFor="name">Name: </label> 
                            <input className = "inputCheckout" type="text" name="name" placeholder="" value={""} disabled/>
                        </div>
                        <div className='rowCheckout'>
                            <label className = "labelCheckout" htmlFor="address">Address: </label> 
                            <input className = "inputCheckout" type="text" name="address" placeholder="" value={""}  disabled/>
                        </div>
                        <div className='rowCheckout'>
                            <label className = "labelCheckout" htmlFor="city">City: </label> 
                            <input className = "inputCheckout" type="text" name="city" placeholder="" value={""}   disabled/>
                        </div>
                        <div className='rowCheckout'>
                            <label className = "labelCheckout" htmlFor="phoneNumber">PhoneNumber: </label> 
                            <input className = "inputCheckout" type="text" name="phoneNumber" placeholder="" vvalue={""} disabled/>
                        </div>
                    <button type="submit">Update product</button>
                </form>
            }
        </Container>

    );
}

   export default MyCart; 






