import React, {useState, useEffect} from 'react';
import { Container } from './MyProductStyle';

const MyProduct = () =>   {

    const accountID  = sessionStorage.getItem("FarmerId");

    const [productList, setProductList] = useState([]);

    useEffect(()=>{

        fetch('http://localhost/backend/getListOfProductByFarmer.php', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                FarmerID: accountID
            }) 
        })
        .then( (response) => {
            return response.json()
        }).then( (data) => {
            setProductList(data);
        });

    },[accountID]);

    const handleDelete  = async (product, key) =>{
        await fetch('http://localhost/backend/deleteProduct.php', {
            method: 'DELETE',
            headers: {
            'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                ProductID: product.ProductID
            }) 
        }) ;

        alert('Deleted product.');
        productList.splice(key,1);
        setProductList([...productList]);
    } 

    return (
        <Container>
            <h1>My Products</h1>
            <table>
                <tr>
                    <th>Name</th>
                    <th>Description</th>
                    <th>Price</th>
                    <th>Quantity</th>
                    <th>Category</th>
                    <th></th>
                </tr>
                {productList.map((product, key) => {
                    return (
                        <tr key={product.ProductID}>
                        <td>{product.ProductName}</td>
                        <td>{product.ProductDescription}</td>
                        <td>{product.ProductPrice}</td>
                        <td>{product.ProductQuantity}</td>
                        <td>{product.ProductCategory}</td>
                        <td><button id='btnEdit'>Edit</button> <button onClick={() => handleDelete(product, key)} id='btnDelete'>Delete</button></td>
                        </tr>
                    )
                })}
            </table>
        </Container>

    );
}

   export default MyProduct;
