import React,{useContext, useState} from 'react';
import {Container} from './ProductCardStyle';
import { AppContext } from './AppProvider'
// import '../Styles/productCard.css';



function CustomerListOfProduts (props) {
    const { cart, setCart , cartPrice, setCartPrice} = useContext(AppContext);
    const [qty, setQty] = useState(0);

    const handleAdd  =  (product, key) =>{
        console.log("entrou");
        setQty(qty+1);
        setCart([...cart,product]);
        setCartPrice(cartPrice + product.ProductPrice);
    }

    const handleRemove  =  (product, key) =>{
        setQty(qty-1);
        const index = cart.findIndex(el => el.ProductID === product.ProductID);
        const newCart = cart.splice(1,index);
        setCart(newCart);
        setCartPrice(cartPrice - product.ProductPrice);
    }

    const findItem = (id) =>{
        return cart.some(item => id === item.ProductID)
    }

    return(

        <Container>
            <div className="blockItem">
                <div>
                <img src={props.productItem.ProductImageURL} className="ImgItem" alt="ImgItem"/>
                </div>
                <p className="ItemName">{props.productItem.ProductName}</p>
                <p className="ItemPrice">$ {props.productItem.ProductPrice}</p>
                {!findItem(props.productItem.ProductID) && <button className="btnItem" onClick={() => handleAdd(props.productItem, props.key)} >ADD</button>}
                {cartPrice > 0 && findItem(props.productItem.ProductID) &&
                    <div id='groupButtonCart'>
                        <button id="btnAdd" onClick={() => handleAdd(props.productItem, props.key)} >+</button>
                        <button id="btnRemove" onClick={() => handleRemove(props.productItem, props.key)} >-</button>
                    </div> 
                }
                <p>Qty: {qty} </p>
            </div>
        </Container>
    );
}

export default CustomerListOfProduts;
