import React, {Component}from "react";
import CustomerHeader from './CustomerHeader';
import CustomerMenu from './CustomerMenu';
import CustomerListOfProduts from "./CustomerListOfProduts";



class Customer extends Component  {

    constructor(props) {
        super(props);
        this.state = {
            user_id: '',
            userName: '',
            hasSession: false
        }; 
    }
    componentDidMount() {
        this.validateSession();
    }
    validateSession() {
        if(sessionStorage.getItem("id")){
            that.setState({
                user_id: sessionStorage.getItem("id")
            })
            this.getUser();
        }

    };

    getUser () {
        var that = this;
        fetch('http://localhost/backend/getUserById.php', {
            method: 'GET',
            headers: {
            'Content-Type': 'application/json'
            },body: JSON.stringify({
                user_id: this.state.user_id
            }) 
        }).then( (response) => {
            return response.json()
        })
        .then( (data) => {
            that.setState({
                userName: data.firstName,
                hasSession: true
            })


        }).catch(() => this.setState({...that.state, message: 'Usuario invalidooo'}));
    }



    render() {

    return(
        <>
            <CustomerHeader name = {this.state.userName} session = {this.state.hasSession}/>
            <CustomerMenu/>
            <CustomerListOfProduts/>
        </>

    );
    }
}

export default Customer;
