import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import logoMarket from "../Images/Logo_MarketPlace.png"
import '../Styles/createAccount.css';

const AddProduct = () =>   {


    const [productName, setProductName] = useState("");
    const [productDescription, setProductDescription] = useState("");
    const [productPrice, setProductPrice] = useState("");
    const [productQuantity, setProductQuantity] = useState("");
    const [productCategory, setProductCategory] = useState("");
    const [selectedFile, setSelectedFile] = useState(null);
    const [message, setMessage] = useState("");

   const handleSubmit = event => {
        event.preventDefault();

        const formData = new FormData();
        formData.append("productName", productName);
        formData.append("productDescription", productDescription);
        formData.append("productPrice", productPrice);
        formData.append("productQuantity", productQuantity);
        formData.append("productCategory", productCategory);
        formData.append("file", selectedFile);

        // fetch('http://localhost:8080/api/users', {
        fetch('http://localhost/backend/addProduct.php', {
            method: 'POST',
            headers: {
            'Content-Type': 'application/json'
            },
            body: formData
        })
        .then( (response) => {
            return response.json()
        }).then( (data) => {
            setMessage("User created");
            alert('Product created successfully!!');
        });
    }; 

    return (
        <div className='createAccountPage'>
        <h1>Register a new product</h1>
        <form  className="formAddProduct" >
            <label className = "labelAddProduct" htmlFor="productName">Name: </label> 
            <input type="text" name="productName" placeholder="Product Name" value= '' onChange={setProductName} required/>
            <br/>
            <label className = "labelAddProduct" htmlFor="productDescription">Description: </label> 
            <input type="text" name="productDescription" placeholder="Product Description" value='' onChange={setProductDescription} required/>
            <br/>
            <label className = "labelAddProduct" htmlFor="productPrice">Price: </label> 
            <input className = "inputAddProduct" type="text" name="productPrice" placeholder="Product Price" value='' onChange={setProductPrice} required/>
            <br/>
            <label className = "labelAddProduct" htmlFor="productQuantity">Quantity: </label> 
            <input className = "inputAddProduct" type="text" name="productQuantity" placeholder="Product Quantity" value= '' onChange={setProductQuantity} required/>
            <br/>
            <label className = "labelAddProduct" htmlFor="productCategory">Category </label> 
            <input className = "inputAddProduct" type="text" name="productCategory" placeholder="Product Category" value='' onChange={setProductCategory} required/>
            <br/>
            <label className = "labelAddProduct" htmlFor="productImageURL">Upload the product image: </label> 
            <input type="file" value={selectedFile} onChange={(e) => setSelectedFile(e.target.files[0])}/>

            <button onSubmit={handleSubmit} type="submit">Creat account</button>
            <p>{message}</p>
        </form>
        <Link className='linkTo' to="/login">Go back to Login</Link>
        </div>
    );
}

   export default AddProduct;
