import React, {useState, useEffect} from 'react';
import { useLocation } from 'react-router-dom';
import ProductCard from './ProductCard';
import '../Styles/productFeed.css';

const ProductsFeed = () =>   {

    const location = useLocation();
    const { categoryType } = location.state


    const [productList, setProductList] = useState([]);
    const [message, setMessage] = useState("");

    useEffect(()=>{
        if(categoryType == "All"){
            fetch('http://localhost/backend/listOfProduct.php', {
                method: 'GET',
                headers: {
                'Content-Type': 'application/json'
                }
            })
            .then( (response) => {
                return response.json()
            }).then( (data) => {
                console.log(data);
                setProductList(data.slice(0,4));
            }).catch(() => setMessage("Product Registed"));
        }
        // else{
        //     fetch('http://localhost/backend/getProduct.php', {
        //         method: 'POST',
        //         headers: {
        //         'Content-Type': 'application/json'
        //         },
        //         body: JSON.stringify({
        //             ProductCategory: category
        //         }) 
        //     })
        //     .then( (response) => {
        //         return response.json()
        //     }).then( (data) => {
        //         console.log(data);
        //         setProductList(data);
        //     }).catch(() => setMessage("Product Registed"));
        // }


    },[]);

    return (
        <ul className='productFeedPage'>
            {productList.map(product => (<ProductCard className="productItem" key={product.ProductID} productName= {product.ProductName} 
            productImage={product.ProductImageURL} productPrice={product.ProductPrice}/>))}
        </ul>
    );
}

   export default ProductsFeed;
